function authFunction(uname, password, $http, $rootScope, success, error) {

  var authSuccess = success;
  var authError = error;

  //Powercampus Authentication

  var url = "https://kryptosmw.kryptosmobile.com/webadapter2/services/authenticate/IND1168/PC";
  var data = {};
  if (window.device) {
    data = "username=" + uname + "&password=" + password;
  } else {
    url = "/websimulator/json?url=" + url;
    data = {
      method: "POST",
      body: "username=" + uname + "&password=" + password
    };
  }
  var authUrl = url;
  var authData = data;
  $rootScope.generateST = function(tgt, service, callback) {
    //Bypassing the ST as there is no service ticket required here..
    callback(tgt);
  };

  $rootScope.postAPI = function(endpoint, postdata, callback) {
    $.blockUI();
    $rootScope.generateST($rootScope.ticket, endpoint, function(st) {
      var serviceurl = endpoint + "?ticket=" + st;
      //alert("service URL with ticket line 30: " + JSON.stringify(serviceurl));
      var url = "";
      var proxyMethod = "POST";
      var proxyData = postdata;
      if (window.device) {
        url = serviceurl;
      } else {
        url = "/websimulator/json?url=" + serviceurl;
        proxyMethod = "POST";
        proxyData = {
          method: "POST",
          body: postdata
        }
      }
      $http({
        method: proxyMethod,
        url: url,
        data: proxyData
      }).
      success(function(data, status, headers, config) {
        $.unblockUI();
        callback(data, status, headers, config);
      }).
      error(function(data, status, headers, config) {
        //alert("Error in service call line 54: " + data + " Status : " + status);
        if (status == 403) { //Ticket expired.. need to relogin
          $http.post(authUrl, authData).success(function(data, status, headers, config) {
            authSuccess(data);
            $rootScope.postAPI(endpoint, postdata, callback);
          }).error(function(data, status, headers, config) {
            authError(data);

          });
        } else {
          $.unblockUI();
        }

      });

    });

  };

  $rootScope.getAPI = function(endpoint, callback) {
    $.blockUI();
    $rootScope.generateST($rootScope.ticket, endpoint, function(st) {
      var serviceurl = endpoint + "?ticket=" + st;
      var url = "";
      var proxyMethod = "GET";
      var proxyData = {};
      if (window.device) {
        url = serviceurl;
      } else {
        url = "/websimulator/json?url=" + encodeURIComponent(serviceurl);
        proxyMethod = "POST";
        proxyData = {
          method: "GET"
        }
      }
      $http({
        method: proxyMethod,
        url: url,
        data: proxyData
      }).
      success(function(data, status, headers, config) {
        $.unblockUI();
        callback(data, status, headers, config);
      }).
      error(function(data, status, headers, config) {

        //alert("Error in service call line 100: " + data + " Status : " + status);
        if (status == 403) { //Ticket expired.. need to relogin
          $http.post(authUrl, authData).success(function(data, status, headers, config) {
            authSuccess(data);
            $rootScope.getAPI(endpoint, callback);
          }).error(function(data, status, headers, config) {
            authError(data);

          });
        } else {
          $.unblockUI();
        }

      });

    });

  };




  $.blockUI({
    message: '<div id="floatingBarsG"><div class="blockG" id="rotateG_01"></div><div class="blockG" id="rotateG_02"></div><div class="blockG" id="rotateG_03"></div><div class="blockG" id="rotateG_04"></div><div class="blockG" id="rotateG_05"></div><div class="blockG" id="rotateG_06"></div><div class="blockG" id="rotateG_07"></div><div class="blockG" id="rotateG_08"></div></div><div><h3 style="color:#E6E6E6">Authenticating</h3></div>'
  });

  $http.post(url, data).success(function(data, status, headers, config) {
    success(data);
    $.unblockUI();
  }).error(function(data, status, headers, config) {
    error(data);
    $.unblockUI();
  });

}

